import React, { memo, useState } from 'react';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Paragraph from '../components/Paragraph';
import Button from '../components/Button';
import * as ImagePicker from 'expo-image-picker';
import { Image, Platform } from 'react-native';


const Dashboard = ({ navigation, route }) => {

  const [photo, setPhoto] = useState(null);

  const _logout = () => {
    fetch('http://159.89.153.162:5000/api/v1/auth/logout', {
      method: 'GET'
    });
    global.Token = '';
    navigation.navigate('Home');
  }

  const _getUserAndNavigate = () => {
    fetch('http://159.89.153.162:5000/api/v1/auth/me', {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + global.Token
      }
    }).then((response) => response.json())
    .then((json) => {
      console.log(json);
      navigation.navigate('UpdateUserInformation', { user: json.data });
    });
  }

  const _getProfileAndNavigate = () => {
    fetch('http://159.89.153.162:5000/api/v1/profile/me', {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + global.Token
      }
    }).then((response) => response.json())
    .then((json) => {
      if (json.success !== undefined && json.success === false) {
        navigation.navigate('CreateUpdateProfile', { action: 'Create' });
      } else {
        navigation.navigate('CreateUpdateProfile', { action: 'Update' });
      }
    });
  }

  const _handleChoosePhoto = async () => {
    try {
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });
      if (!result.cancelled) {
        setPhoto(result);
      }
      console.log(result);
    } catch (error) {
      console.log(error);
    }
  };

  const _getPhoto = () => {
    fetch('http://159.89.153.162:5000/api/v1/profile/0001/photo', {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + global.Token
      }
    });
  }

  const _uploadPhoto = () => {
    var data = new FormData();
    data.append('file', {
      uri: photo.uri,
      type: 'image/jpeg',
      name: 'photo.jpg'
    });
    fetch('http://159.89.153.162:5000/api/v1/profile/0001/photo', {
      method: 'PUT',
      headers: {
        'Content-Type': 'multipart/form-data;',
        'Authorization': 'Bearer ' + global.Token
      },
      body: data
    });
  }

  return (
    <Background>
      <Header>Letâ€™s start</Header>
      <Button mode="outlined" onPress={_logout}>
        Logout
      </Button>
      <Button mode="outlined" onPress={_getUserAndNavigate}>
        Update User
      </Button>
      <Button mode="outlined" onPress={_getProfileAndNavigate}>
        Create/Update Profile
      </Button>
      {photo && (
        <Image
          source={{ uri: photo.uri }}
          style={{ width: 300, height: 300 }}
        />
      )}
      <Button mode="outlined" onPress={_handleChoosePhoto}>
        Choose Photo
      </Button>
      <Button mode="outlined" onPress={_uploadPhoto}>
        Send Photo
      </Button>
      <Button mode="outlined" onPress={_getPhoto}>
        Get Photo
      </Button>
    </Background>
  );
}

export default memo(Dashboard);
